SourceCode.Declaration:
/// <summary>
/// Extends the Batch job tasks and logs telemetry events for the batch job status updates, for update and insert it will only record cancelled record, and the move to "executing"
/// Anything else is logged by the eventhandler when it finishes
/// </summary>
[ExtensionOf(tableStr(BatchJob))]
public final class MyBatchJob_Extension
{
}

Method.Source:
public void update()
{
    try
    {
        if(this.Status != this.orig().Status)
        {
            MyTelemetryBatch batch = new MyTelemetryBatch(this.orig(), this);
            batch.processEvent(MyTelemetryEventNames::BatchJobStatusUpdate);
        }
    }
    catch
    {

        //do nothing telemetry should not stop the batch job
    }
    next update();

}

public void insert()
{
    try
    {
        runAsync(classNum(MyTelemetryBatch), staticMethodStr(MyTelemetryBatch, logBatchJobAsync), [this, this, MyTelemetryEventNames::BatchJobCreated]);
    }
    catch
    {
        //do nothing telemetry should not stop the batch job
    }

    next insert();

}
