/// <summary>
/// Extends the Batch table to send telemetry when the status of the batch is updated
/// </summary>
[ExtensionOf(tableStr(Batch))]
public final class MyBatch_Extension
{

}

Method.Source:
public void update()
{

    try
    {
        if(this.Status != this.orig().Status)
        {
            MyTelemetryBatch batch = new MyTelemetryBatch(this.orig(), this);
            batch.processEvent(MyTelemetryEventNames::BatchStatusUpdate);
        }
    }
    catch
    {
        //Do nothing, telemetry should not stop the process
    }



    next update();

}
